class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int n=s.length();
        int zeroplane=0;
        if(s.length()==1){
            return 0;
        }
        for(int i=n-2;i>=0;i--){
            if(s[i]=='1'&&s[i+1]=='0'){
                zeroplane++;
                ans+=zeroplane;
            }
            if(s[i]=='1'&&s[i+1]=='1'){
                ans+=zeroplane;
            }
        }
        return ans;
    }
};
