class Solution {
public:
    int maxOperations(string s) {
        if(s.back() != '1') s.push_back('1');
        int n = size(s);
        int last_seen = -1, cnt = 0;
        int answer = 0;
        for(int i = 0; i < n; ++i){
            if(s[i] == '0'){
                continue;
            }
            if(last_seen != -1){
                int dist = i - last_seen - 1;
                if(dist > 0) answer += cnt;
            }
            last_seen = i;
            cnt++;
        }
        return answer;
    }
};
