class Solution:
    def maxOperations(self, s: str) -> int:
        ones = 0
        move = 0
        n = len(s)
        for pnt in range(n):
            if s[pnt] == '0' and (pnt == n - 1 or s[pnt+1] == '1'):
                move += ones
            elif s[pnt] == '1':
                ones += 1
        return move
