class Solution:
    def maxOperations(self, s: str) -> int:

        s = '0' + s

        ones_count = 0
        res = 0
        for i in range(1, len(s)):
            if s[i] == '1':
                if s[i - 1] == '0':
                    # ones_count = 0
                    # pass
                # else:
                    res += ones_count
                ones_count += 1
            # else:
            #     pass

        if s[-1] == '0':
            res += ones_count
        return res
