class Solution {
public:
    int maxOperations(string s) {
        int n=s.size()-1;
        while(n>=0 && s[n]=='1'){
            n--;
        }
        int total=0,ans=0;
        while(n>=0){
            if(s[n]=='1' && s[n+1]=='0'){
                total++;
            }
            if(s[n]=='1'){
                ans=ans+total;
            }
            n--;
        }
        return ans;
    }
};
