class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.size();
        int numberOfOnes = 0;
        for(int i = 0; i < n; ++i) {
            if(s[i] == '1') {
                numberOfOnes++;
            }
            if(i != 0) {
                if(s[i] == '0' && s[i - 1] == '1') {
                    ans += numberOfOnes;
                }
            }
        }
        return ans;
    }
};
