class Solution:
    def maxOperations(self, s: str) -> int:
        i=0
        s=list(s)
        n=len(s)
        r=0
        o=0
        z=0
        while(i<n):
            if(s[i]=='0'):
                z=1
            else:
                if(z):
                    r+=o
                    z=0
                o+=1

            i+=1
        if(z):
            r+=o
        return r
