class Solution:
    def maxOperations(self, binaryString: str) -> int:
        total_operations = 0
        current_count = 0
        encountered_one = False
        binary_sequence = []

        for character in binaryString:
            if character == '1':
                binary_sequence.append(1)
                encountered_one = True
            elif character == '0' and encountered_one:
                binary_sequence.append(0)
                encountered_one = False

        for bit in binary_sequence:
            if bit == 1:
                current_count += 1
            else:
                total_operations += current_count

        return total_operations
