class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> ones;
        // cout << n << endl;
        for (int i = 0; i < n;) {
            for (; i < n && s[i] == '0'; ++i);
            if (i == n) break;
            int j = i + 1;
            for (; j < n && s[j] == '1'; ++j);
            ones.push_back(j - i);
            // cout << ones.back() << endl;
            i = j;
        }
        if (s[n - 1] == '1') ones.resize(ones.size() - 1);
        int ans = 0;
        int now = 0;
        for (int one : ones) {
            now += one;
            ans += now;
        }
        return ans;
    }
};
