def find_1_islands(s):
    islands = []
    current_count = 0

    for char in s:
        if char == '1':
            current_count += 1
        else:
            if current_count > 0:
                islands.append(current_count)
                current_count = 0

    return islands


class Solution(object):
    def maxOperations(self, s):
        """
        :type s: str
        :rtype: int
        """
        islands = find_1_islands(s)

        triangle = 0
        ans = 0
        for island in islands:
            triangle += island
            ans += triangle

        return ans
