class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        cnt0 = 1 if s[-1] == '0' else 0
        n = len(s)
        last0 = n if s[-1] == '1' else n-1
        for i in range(n-2, -1, -1):
            if s[i] == '1':
                ans += cnt0
            else:
                if i + 1 != last0:
                    cnt0 += 1
                last0 = i
        return ans
