class Solution {
    public int maxOperations(String s) {
         int totalOperations = 0;
        int countOfOnes = 0;
        boolean isOneSeen = false;

        for (int index = 0; index < s.length(); index++) {
            if (s.charAt(index) == '1') {
                countOfOnes++;
                isOneSeen = true;
            } else {
                if (isOneSeen) {
                    totalOperations += countOfOnes;
                }
                isOneSeen = false;
            }
        }

        return totalOperations;
    }
}
