class Solution {
public:
    int maxOperations(string s) {
        vector<int> curr_pos,final_pos;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){curr_pos.push_back(i);}
        }
        if(curr_pos.size()==0){return 0;}
        int x=s.size()-1;
        while(final_pos.size()<curr_pos.size()){
            final_pos.push_back(x);
            x--;
        }
        reverse(final_pos.begin(),final_pos.end());
        vector<int> steps(curr_pos.size(),0);
        for(int i=curr_pos.size()-1;i>=0;i--){
            if(i==curr_pos.size()-1){
                if(curr_pos[i]!=final_pos[i]){steps[i]=1;}
            }
            else{
                if(curr_pos[i]+1==curr_pos[i+1]){steps[i]=steps[i+1];}
                else{
                    steps[i]=1+steps[i+1];
                }
            }
        }
        int ans=0;
        for(int i=0;i<steps.size();i++){ans+=steps[i];}
        return ans;
    }
};
