class Solution {
    public int maxOperations(String s) {
        int yans=0,cnt=0;
        boolean cheak=true;
        for(int i=0;i<s.length();i++){
            if(s.charAt(i)=='1'){
                cnt++;
                cheak=true;
            }
            else{
                if(cheak){
                    yans+=cnt;
                }
                cheak=false;
            }
        }
        return yans;
    }
}
