class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int numOfOnes = 0;
        int i=0;
        while (i<s.length()){
            if (s[i]=='1'){
                numOfOnes++;
                i++;
            } 
            else {
                ans+=numOfOnes;
                while (i < s.length() && s[i]!='1') i++;
            }
        }
        return ans;
    }
};
