class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int p = 0;
        bool flag = false;
        vector<int> v;

        for (char c : s) {
            if (c == '1') {
                v.push_back(1);
                flag = true;
            } else if (c == '0' && flag) {
                v.push_back(0);
                flag = false;
            }
        }

        for (int x : v) {
            if (x==1) {
                p++;
            } else {
                ans += p;
            }
        }

        return ans;
    }
};
