class Solution {
    public int maxOperations(String s) {
        char ch[]=s.toCharArray();
        int ans=0;
        boolean check=false;
        int ones=0;
        for(int i=0;i<ch.length;i++)
        {
            if(ch[i]=='1')
            {
                ones++;
                check=true;
            }
            else if(ch[i]=='0' && check)
            {
                ans+=ones;
                check=false;
            }
        }
        return ans;
    }
}
