class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int o = 0;
        int z = 0;
        int res = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                z += 1;
            } else {
                if (z > 0) {
                    res += o;
                    z = 0;
                }
                o += 1;
            }
        }
        if (z > 0) {
            res += o;
        }
        return res;
    }
};
