class Solution {
public:
    int maxOperations(string s) {
		int n = s.size();
		int bl = 0;
		int cnt = 0;
		int ans = 0;
		for (int i = 0; i < n; ++i) {
			if (s[i] == '1') ++bl;
			if (i + 1 == n || s[i] == '1' && s[i + 1] == '0') ans += cnt, cnt += bl, bl = 0;
		}
		return ans;
    }
};
