class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int n = (int) s.size();
        int one = 0;

        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                one++;
            }
            else if (i > 0 && s[i - 1] != '0') {
                res += one;
            }
        }

        return res;
    }
};
