class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        bool zero=false;
        int prev=0;
        for(int i=s.length();i>=0;i--){
            if(s[i]=='0'){
                zero=true;
            }
            else{
                if(zero && s[i+1]=='0'){
                    int d=prev;
                    prev=d+1;
                    cnt+=d+1;
                }
                else if(zero && s[i+1]=='1'){
                    cnt+=prev;
                }
            }
        }
        return cnt;
    }
};
