class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int cnt=0;
        int ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0'&&i-1>=0&&s[i-1]=='1'){
                ans+=cnt;
            } else if(s[i]=='1'){
                cnt++;
            }
        }
        return ans;
    }
};
