class Solution {
    public int maxOperations(String s) {

        char before = '2';
        int cnt = 0;
        int result = 0;
        int suffix[] = new int[s.length()];
        for(int i = s.length()-1; i >= 0; i--) {
            if ( s.charAt(i) == '0' ){
                if ( before != '0' ) {
                    before = '0';
                    cnt++;
                }
            } else{
                before = '1';
            }
            suffix[i] = cnt;
        }

        // for(int i = 0; i < suffix.length; i++) System.out.print(suffix[i] + " "); System.out.println();

        for(int i = 0; i < suffix.length; i++) {
            if ( s.charAt(i) == '1' ) result += suffix[i];
        }

        return result;
    }
}
