class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ones = 0;
        int n = s.length();
        if(s[0]=='1')
            ones++;
        for(int i = 1; i < n; i++){
            if(s[i]=='1')
                ones++;
            else if(s[i]=='0' && s[i-1]=='1'){
                ans += ones;
            }
        }

        return ans;
    }
};
