class Solution {
    public int maxOperations(String s) {
        HashSet<Integer>hs=new HashSet<>();
        for (int i = s.length()-1; i >=0 ; i--) {
            if(s.charAt(i)=='0'){
                if(i==s.length()-1||s.charAt(i+1)=='1'){
                    hs.add(i);
                }
            }
        }
        int count=0;
        int ans=0;
        for (int i = 0; i < s.length(); i++) {
            if(s.charAt(i)=='1'){
                count++;
            }else if(hs.contains(i)){
                ans+=count;
            }
        }
        return ans;
    }
}
