class Solution {
    public int maxOperations(String s) {
        int pre = 0 ;
        int ans = 0;
        for (int i = 0; i < s.length() ;i++){
            if (s.charAt(i) == '1'){
                pre++;
            }else{
                ans += pre;
                while (i < s.length() && s.charAt(i) == '0'){
                    i++;
                }
                i--;
            }
        }
        return ans;
    }
}
