class Solution {
public:
    int maxOperations(string s) {
        s.push_back('1');

        int one_cnt = 0, ans = 0;
        for(int i = 0; i < s.size() - 1; i++){
            if(s[i] == '1')
                one_cnt++;

            if(s[i] == '1' && s[i + 1] == '0')
                ans += one_cnt;
        }

        return ans;
    }
};
