class Solution:
    def maxOperations(self, s: str) -> int:
        groups = deque()
        prev = '0'
        for i, n in enumerate(s):
            if i == len(s)-1 and n=='0':
                groups.appendleft(0)
            elif prev=='0' and n=='1':
                groups.appendleft(1)
            elif prev=='1' and n=='1':
                groups[0] += 1
            prev = n
        ans = 0
        for i, n in enumerate(groups):
            ans += i*n
        return ans
