class Solution {
public:
    vector<int> p, sz;
    int maxOperations(string s) {
        int n = s.size();
        p.resize(n);
        sz.resize(n);
        for(int i = 0; i < n; i++) {
            p[i] = i;
            sz[i] = 1;
        }
        for(int i = 0; i < n; i++) {
            if(s[i] == '1' && i + 1 < n && s[i + 1] == '1') {
                merge(i, i + 1);
            }
        }
        int ans = 0, cnt = 0;
        for(int i = 0; i < n;) {
            if(s[i] == '1') {
                if(find(i) != n - 1) {
                    cnt += sz[find(i)];
                    ans += cnt;
                }
                i = find(i) + 1;
            }else {
                i++;
            }
        }
        return ans;
    }

    int find(int x) {
        return x == p[x]? x: p[x] = find(p[x]);
    }

    void merge(int x, int y) {
        int rx = find(x), ry = find(y);
        if(rx != ry) {
            sz[ry] += sz[rx];
            p[rx] = ry;
        }
    }
};
