class Solution {
public:
    int maxOperations(string s) {
        int c = 0;
        int v = 0;
        int l = -1;
        for(int i = 0; i < s.size() ; i++)
        {   
            int nec = 0;
            while(i < s.size() && s[i] == '1')
            {
                nec++;
                i++;
            }
            if(nec> 0)
            {
                 v += c;
                 c += nec;
            }
        }

        if(s[s.size() - 1] == '0')
        {
             v+=c;
        }
        return v;
    }
};
