class Solution {
public:
    int maxOperations(string s) {
        string a;
        int n=s.size();
        for(int i=0;i<s.size();){
            if(s[i]=='1') {
                a.push_back(s[i]);
                i++;
            }
            else{
                while(i<n&&s[i]=='0') i++;
                a.push_back('0');
            }
        }
        int ans=0,cur=0;
        n=a.size();
        for(int i=n-1;i>=0;i--){
            if(a[i]=='0') cur++;
            else ans+=cur;
        }
        return ans;
    }
};
