class Solution {
public:
    int maxOperations(string s) {
        ios_base::sync_with_stdio(false); cin.tie(NULL); cout.tie(NULL);
        int n = s.size(), c = 0, ans = 0;
        for ( int i = n-2; i>=0; i-- ){
            if ( s[i] == '1' && s[i+1] == '1' ) ans += c;
            if ( s[i] == '1' && s[i+1] == '0' ) {
                c++;
                ans += c;
            }
        } return ans;
    }
};
