class Solution {
public:
    int maxOperations(string s) {
        int p = 0, ans = 0, tmpcnt = 0;
        while (p < s.size()) {
            if (s[p] == '1') {
                ans += tmpcnt;
                while (p < s.size() && s[p] == '1') {
                    tmpcnt += 1;
                    p += 1;
                }
            }
            p += 1;
        }
        if (s[s.size() - 1] == '0') {
            ans += tmpcnt;
        }
        return ans;
    }
};
