class Solution {
public:
    int maxOperations(string s) {
        s.push_back('1');
        int n = s.size();
        int i = 0;
        vector<int> cnts;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') continue;
            int j = i;
            while (j < n && s[j] == '1') {
                j++;
            }
            cnts.push_back(j - i);
            i = j - 1;
        }
        int ans = 0;
        for (int i = 0; i < cnts.size() - 1; i++) {
            ans += cnts[i] * (cnts.size() - i - 1);
        }
        return ans;
    }
};
