class Solution {
    public int maxOperations(String s) {
        int result = 0, n = 0;
        boolean flag = true;

        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                n++;
                flag = true;
            } else {
                if (flag) result += n;
                flag = false;
            }
        }

        return result;
    }
}
