class Solution {
public:
    int maxOperations(string s) {
        int final_answer = 0;
        int consecutive_zeros = 0;
        int found_one = 0;
        vector<int> track;
        for (char x : s){
            if (x - '0' == 1){
                track.push_back(1);
                found_one = 1;
            }
            else if (x - '0' == 0 && found_one){
                track.push_back(0);
                found_one = false;
            }
        }
        for (int x : track){
            if (x == 1){
                consecutive_zeros++;
            }
            else{
                final_answer += consecutive_zeros;
            }
        }
        return final_answer;
    }
};
