class Solution:
    def maxOperations(self, s: str) -> int:
        s_band = s.split('0')

        if s.endswith('1'):
            s_band.pop()

        answer = 0
        seen = 0

        for b in s_band:
            if not b:
                continue

            seen += len(b)
            answer += seen

        return answer
