class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int one = 0;
        int len = s.size();
        int last = -1;
        for(int i = 0; i < len; ++i)
        {
            if(s[i] == '1')
            {
                if(last != -1)
                {
                    if(i-last > 1)
                    {
                        res += one;
                    }
                }
                last = i;
                ++one;
            }
        }
        if(s[len-1] == '0')
            res += one;
        return res;
    }
};
