#include <iostream>
#include <vector>
#include <string>
using namespace std;

class Solution {
public:
    int maxOperations(string s) {
        int operations = 0, onesCount = 0;
        bool foundOne = false;
        vector<int> pattern;

        // Collect 1s and 0s in pattern vector
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                pattern.push_back(1);
                foundOne = true;
            }
            if (s[i] == '0' && foundOne) {
                pattern.push_back(0);
                foundOne = false;
            }
        }

        // Count the operations needed
        for (auto value : pattern) {
            if (value == 1) {
                onesCount++;
            } else {
                operations += onesCount;
            }
        }

        return operations;
    }
};
