class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        pre = 0
        i = 0
        while i < len(s) - 1:
            if s[i] == '1':
                pre += 1
            i+=1
            if s[i] == '0':
                ans += pre
                while i < len(s) - 1 and s[i] == '0':
                    i += 1
        return ans
