class Solution {
public:
    int maxOperations(string s) {
        int count=0;
        int one=0;
        bool flag=false;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'&&flag){
                count+=one;
                flag=false;
                one++;
            }else if(s[i]=='1'){
                one++;
            }else if(s[i]=='0'){
                flag=true;
            }
        }
        if(flag){
            count+=one;
        }
        return count;
    }
};
