class Solution {
public:
    int maxOperations(string s) {
    int ans = 0;
    int p = 0;
    bool hasOne = false;
    vector<int> bits;

    for (char c : s) {
        if (c == '1') {
            bits.push_back(1);
            hasOne = true;
        } else if (c == '0' && hasOne) {
            bits.push_back(0);
            hasOne = false;
        }
    }

    for (int bit : bits) {
        if (bit == 1) {
            p++;
        } else {
            ans += p;
        }
    }

    return ans;
}
};
