class Solution {
public:
    int maxOperations(string s) {
        int one=0,i=0,ans=0;
        while(i<s.size()){
            if(s[i]=='1'){
                one++;
                i++;
            }
            else{
                while(i<s.size() && s[i]=='0'){
                    i++;
                }
                ans+=one;
            }
        }
        return ans;
    }
};
