class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        StringBuilder ns = new StringBuilder();
        boolean zt = false;

        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == '0' && !zt) {
                ns.append('0');
                zt = true;
            } else if (s.charAt(i) == '1') {
                ns.append('1');
                zt = false;
            }
        }

        int len = ns.length();
        int[] ps = new int[len];
        ps[0] = ns.charAt(0) == '0' ? 0 : 1;

        for (int i = 1; i < len; i++) {
            ps[i] = (ns.charAt(i) - '0') + ps[i - 1];
        }

        int ops = 0;
        for (int i = len - 1; i >= 0; i--) {
            if (ns.charAt(i) == '0') {
                ops += ps[i];
            }
        }

        return ops;
    }
}
