class Solution {
    public int maxOperations(String s) {
        int operations = 0;
        char[] arr = s.toCharArray();
        int mul = 1;
        int index = arr.length - 1;

        while (index >= 0 && arr[index] == '1') {
            index--;
        }

        while (index >= 0) {
            int count = 0;

            while (index >= 0 && arr[index] == '1') {
                count++;
                index--;
            }

            operations += count * mul;
            index--;

            if (count != 0) {
                mul++;
            }
        }

        return operations;
    }
}
