class Solution {
public:
    int maxOperations(string s) {
        vector<int> dp(s.length() + 1, 0);
        int n = s.length() - 1;
        dp[n] = (s[n] == '0');
        int answer = 0;
        for (int i = n - 1; i>= 0 ;i --)
        {
            if (s[i] == '1') {
                dp[i] = dp[i + 1];
                answer += dp[i];
            }
            else {
                if (s[i + 1] == '0') dp[i] = dp[i + 1];
                else dp[i] = dp[i + 1] + 1;
            }
        }

        return answer;
    }
};
