class Solution {
public:
    int maxOperations(string s) {
        int count_one = 0, count = 0;

        for(int i=0;i<s.size();i++)
        {
            char c = s[i];
            if(c == '1')
                count_one++;
            else
            {
                count += count_one;
                while(i+1<s.size() && s[i+1] == '0')
                    i++;
            }
        }

        return count;
    }
};
