class Solution:
    def maxOperations(self, s: str) -> int:
        oi = s.find('1')
        if oi == -1:
            return 0

        s = s[oi:]
        ones = 0
        res = 0
        for v,p in groupby(s):
            if v == '1':
                ones += len([*p])
            else:
                res += ones

        return res
