        int l = s.size();

        // int firsti = l;
        // for(int i=0;i<l;i++){
        //     if(s[i] == '1'){
        //         firsti = i;
        //         break;
        //     }
        // }
        vector<int>res;
        for(int i=0;i<l;i++){
            if(s[i] == '0'){
                continue;
            }else{
                int left = i;
                while(s[i] == '1' && i <l) i++;
                if(i - left > 0) res.push_back(i - left);
            }
        }

        cout<<res.size();
        int ans = 0;
        int nowi = 0;
        for(int it : res){
            nowi += it;
            ans += nowi;
        }
        //cout<<
        if(s[l-1] == '1') ans -= nowi;
        return ans;
    }
};
