class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        string res = "";
        int ans = 0, c = 0, one = 0;
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                int j = i;
                while(j<n && s[j]=='0') j++;
                ans+=one;
                i = j-1;
            }
            else one++;
        }
        return ans;
    }
};
